using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace Pyxis.Publishing
{
    /// <summary>
    /// Allow users to connect to www.globalgridsystems.com using an API
    /// </summary>
    public class ApiKey
    {
        /// <summary>
        /// Application Key generated by www.globalgridsystems.com for you application powered by PYXIS innovation
        /// </summary>
        public string ApplicationKey { get; set; }

        /// <summary>
        /// Application Email used to generate the Application Key
        /// </summary>
        public string Email { get; set; }

        public ApiKey() { }

        public ApiKey(string email, string applicationKey)
        {
            Email = email;
            ApplicationKey = applicationKey;
        }
    }
}
